function submitIabConsent(actionButton) {
    const purposes = []
    const features = []
    const vendors = []
    console.log('actionButton', actionButton)
    document.querySelectorAll('.iab-consent-privy-cmp-AE1VSVI8T5').forEach(input => {
        if (input.checked) {
            const dataAttribute = input.getAttribute('data-iab-consent')
            const agreedConsent = input.id
            console.log('agreed iab consent', dataAttribute, agreedConsent)
            if (dataAttribute === 'purpose') {
                purposes.push(agreedConsent)
            } else if (dataAttribute === 'feature') {
                features.push(agreedConsent)
            } else if (dataAttribute === 'vendor') {
                vendors.push(agreedConsent)
            }
        }
    })
    const finalConsent = {
        purposes,
        features,
        vendors
    }
    console.log('finalConsent', finalConsent)
}
